/**
 * アイテム情報リスト初期化.
 */
export function initItems(): _.Dictionary<IItem> {
    const keys: string[] = _.map(master, values => values[0]);
    const values: IItem[] = _.map(master, (values, index): IItem => {
        const result: IItem = {
            addTitle: AddTitleFlg.None,
            category: values[1],
            composite: CompsiteFlg.None,
            displayName: values[0],
            isRare: !(values[2]),
            kind: 'アイテム',
            name: values[0],
            price: Number(values[3]),
            seq: index + 1,
            skills: values.slice(22),
            titleInheritance: TitleInheritanceFlg.None,
        };
        setParameters(result, values.slice(4));
        return result;
    });
    return _.object(keys, values);
}


